mapper.select:
SELECT 
   count(*) AS cnt
FROM tbl_device_agent_job TA JOIN tbl_device_applc TB ON TA.dvc_seq = TB.seq 
WHERE TA.org_seq = #{org_seq}
	AND pcm_uuid = #{pcm_uuid}

select * 
from tbl_device_agent_job
where org_seq = #{org_seq} 
and pcm_uuid = #{pcm_uuid} 
order by insert_dt desc limit 1

SELECT CASE WHEN TB.pcm_uuid is null THEN 'DEL' ELSE 'NOINS' END AS gubun, TA.sm_seq as pcm_seq, TA.org_seq, TA.dvc_seq as pa_seq, TA.pcm_uuid, TA.pcm_name, TA.insert_dt, TB.dvc_seq as old_pa_seq, TB.pcm_uuid as old_pcm_uuid, ( select sm_device_code from tbl_policy_device_list where sm_seq = TA.sm_seq) as device_code FROM tbl_device_agent_job TA LEFT OUTER JOIN ( SELECT * FROM tbl_device_agent_job WHERE dvc_seq = #{old_pa_seq} AND pcm_uuid = #{pcm_uuid} ) TB on TA.sm_seq = TB.sm_seq WHERE TA.org_seq = #{org_seq} and TA.dvc_seq in ( #{new_pa_seq}, #{old_pa_seq} ) and TA.sm_seq in ( ${ppm_seq} ) and TB.pcm_uuid is null and TA.pcm_uuid = #{pcm_uuid} and TA.pcm_name is not null
UNION ALL
SELECT 'INS' as gubun, TA.sm_seq as pcm_seq, TA.org_seq, TA.dvc_seq as pa_seq, TA.pcm_uuid, TA.pcm_name, TA.insert_dt, TB.dvc_seq as old_pa_seq, TB.pcm_uuid as old_pcm_uuid, ( select sm_device_code from tbl_policy_device_list where sm_seq = TA.sm_seq) as device_code FROM tbl_device_agent_job TA LEFT OUTER JOIN ( SELECT * FROM tbl_device_agent_job WHERE dvc_seq = #{old_pa_seq} AND pcm_uuid = #{pcm_uuid} ) TB on TA.sm_seq = TB.sm_seq WHERE TA.org_seq = #{org_seq} and TA.dvc_seq in ( #{new_pa_seq}, #{old_pa_seq} ) and TA.sm_seq not in (${ppm_seq} ) and TA.pcm_name is not null AND TA.pcm_uuid = #{pcm_uuid}
