mapper.update:
update tbl_act_backup_recovery_log
set result = 'Y'
where org_seq = #{org_seq}
	and uuid = #{uuid}

mapper.delete:
begin;
	delete from tbl_updt_agent_job where org_seq = #{org_seq} and pcm_uuid = #{uuid};
	delete from tbl_progrm_agent_job where org_seq = #{org_seq} and pcm_uuid = #{uuid};
	delete from tbl_frwl_agent_job where org_seq = #{org_seq} and pcm_uuid = #{uuid};
	delete from tbl_device_agent_job where org_seq = #{org_seq} and pcm_uuid = #{uuid};
commit;

mapper.select:

select * from tbl_recovery_agent_job 
where org_seq = #{org_seq}
    and pc_uuid = #{pc_uuid} 
order by insert_dt desc     
limit 1
