mapper.insert:
   
		INSERT INTO TBL_ADMIN_LOGIN_HISTORY
		(
		    SEQ
		,   USER_ID  
		,   CONN_IP
		,   LOGIN_DATE    
		)
		VALUES
		(
		    #{loginKey}
		,   #{user_id}   
		,   #{user_ip}     
		,   now()  
		)

mapper.update:
UPDATE TBL_ADMIN_USER
   SET LOGIN_CHECK = LOGIN_CHECK +1
 WHERE USER_ID         = #{user_id}

UPDATE TBL_ADMIN_USER
   SET LOGIN_CHECK = 0
 WHERE USER_ID         = #{user_id}
