mapper.select:
SELECT 
	*
FROM tbl_org

		trim(org_nm) = #{org_nm}
		and trim(domain) = #{domain}

mapper.update:
WITH upsert AS (
	UPDATE TBL_ORG  SET
		P_SEQ			= #{p_seq}				 
		, ORG_ORDR		= #{org_ordr}		
		, ORG_NM		= #{org_nm}		
		, P_ORG_NM		= #{p_org_nm}
		, writer_id		= #{writer_id}
		, WRITER_IP		= #{writer_ip}		
		, UPDATE_WRITER_ID		= #{writer_id}		
		, UPDT_DATE		= now()	
		, UPDATE_WRITER_IP		= #{writer_ip} 
		, SIDO		= #{sido} 
		, GUGUN		= #{gugun} 
		, SECTION		= #{section} 
		, ALL_ORG_NM	= 
		
			#{org_nm}
		
		
			( select  
			STRING_AGG(TA.org_nm, '|' ORDER BY TA.seq ASC) || '|' || #{org_nm}   AS all_org_nm 
			from (
			WITH RECURSIVE T AS
			(
			    SELECT A.*
			    FROM tbl_org A
			    where A.seq = #{p_seq}
					and A.DOMAIN = #{domain}
			    UNION ALL
			    
			   SELECT A.*
			    FROM tbl_org A,  T
			    
			    WHERE A.seq = T.p_seq
					and A.DOMAIN = T.domain
			)
			SELECT seq,domain,p_seq,replace(replace(replace(replace(replace(replace(coalesce(org_nm,p_org_nm),'/',''),'_',''),'(',''),')',''),'+',''),' ','')as org_nm FROM T 
			order by T.seq asc
			
			) TA )
			
		
		WHERE SEQ = #{seq} and DOMAIN = #{domain}	 RETURNING *
)
		
INSERT INTO 
TBL_ORG (	
	SEQ
	, DOMAIN				
	, P_SEQ										   
	, ORG_ORDR						 
	, ORG_NM						
	, P_ORG_NM						  
	, writer_id						
	, RGSTR_DATE						
	, WRITER_IP						
	, UPDATE_WRITER_ID				
	, UPDT_DATE						
	, UPDATE_WRITER_IP				
	, SECTION
	, ALL_ORG_NM
)
SELECT (SELECT COALESCE(max(seq),0)+1 AS seq FROM TBL_ORG where domain = #{domain})
,#{domain},#{p_seq},#{org_ordr},#{org_nm},#{p_org_nm},#{writer_id},now()
,#{writer_ip},#{writer_id},now(),#{writer_ip},#{section},

  #{org_nm}


( 	select STRING_AGG(TA.org_nm, '|' ORDER BY TA.seq ASC) || '|' || #{org_nm} AS all_org_nm 
	from (
		WITH RECURSIVE T AS (
			SELECT A.*
			FROM tbl_org A
			where A.seq = #{p_seq}
			and A.DOMAIN = #{domain}
			UNION ALL
			
			SELECT A.*
			FROM tbl_org A,  T
			
			WHERE A.seq = T.p_seq
			and A.DOMAIN = T.domain
		)
		SELECT domain,seq,p_seq,replace(replace(replace(replace(replace(replace(coalesce(org_nm,p_org_nm),'/',''),'_',''),'(',''),')',''),'+',''),' ','')as org_nm FROM T 
		order by T.seq asc				
	) TA )

WHERE NOT EXISTS (SELECT * FROM upsert)

mapper.select:
select all_org_nm
from tbl_org
where seq = #{org_seq}
and DOMAIN = #{domain}

mapper.update:
update tbl_org
set all_org_nm = #{all_org_nm}
where seq = #{seq}
and DOMAIN = #{domain}
