mapper.select:
select STRING_AGG( 'OU=' || TA.org_nm, ',' ORDER BY TA.seq DESC) AS org_nm from ( WITH RECURSIVE T AS ( SELECT A.* FROM tbl_org A where A.seq = #{seq}
UNION ALL
SELECT A.* FROM tbl_org A, T
WHERE A.seq = T.p_seq ) SELECT seq,p_seq,replace(replace(replace(replace(replace(replace(coalesce(org_nm,p_org_nm),'/',''),'_',''),'(',''),')',''),'+',''),' ','')as org_nm FROM T order by T.seq asc
) TA

select STRING_AGG( 'OU=' || TA.org_nm, ',' ORDER BY TA.seq DESC) AS org_nm from ( WITH RECURSIVE T AS ( SELECT A.* FROM tbl_org A where A.seq = #{p_seq}
UNION ALL
SELECT A.* FROM tbl_org A, T
WHERE A.seq = T.p_seq ) SELECT seq,p_seq,replace(replace(replace(replace(replace(replace(coalesce(org_nm,p_org_nm),'/',''),'_',''),'(',''),')',''),'+',''),' ','')as org_nm FROM T order by T.seq asc
) TA

mapper.update:
WITH upsert AS (
	UPDATE TBL_ORG  SET
		P_SEQ			= #{p_seq}				 
		, ORG_ORDR		= #{org_ordr}		
		, ORG_NM		= #{org_nm}		
		, P_ORG_NM		= #{p_org_nm}
		, writer_id		= #{writer_id}		
		, INS_DATE		= now()    
		, WRITER_IP		= #{writer_ip}		
		, UPDATE_WRITER_ID		= #{writer_id}		
		, UPD_DATE		= now()	
		, UPDATE_WRITER_IP		= #{writer_ip} 
		, SIDO		= #{sido} 
		, GUGUN		= #{gugun} 
		, SECTION		= #{section} 
		, ALL_ORG_NM		= (select  
			STRING_AGG(TA.org_nm, '|' ORDER BY TA.seq ASC) || '|' || #{org_nm}   AS all_org_nm 
			from (
			WITH RECURSIVE T AS
			(
			    SELECT A.*
			    FROM tbl_org A
			    where A.seq = #{p_seq}
			
			    UNION ALL
			    
			   SELECT A.*
			    FROM tbl_org A,  T
			    
			    WHERE A.seq = T.p_seq
			)
			SELECT seq,p_seq,replace(replace(replace(replace(replace(replace(coalesce(org_nm,p_org_nm),'/',''),'_',''),'(',''),')',''),'+',''),' ','')as org_nm FROM T 
			order by T.seq asc
			
			) TA )
		WHERE SEQ = #{seq}	 RETURNING *
)
		
	INSERT INTO 
		TBL_ORG (	
			SEQ						
			, P_SEQ										   
			, ORG_ORDR						 
			, ORG_NM						
			, P_ORG_NM						  
			, writer_id						
			, INS_DATE						
			, WRITER_IP						
			, UPDATE_WRITER_ID				
			, UPD_DATE						
			, UPDATE_WRITER_IP				
			, SIDO						
			, GUGUN						
			, SECTION
			, ALL_ORG_NM
		)
	SELECT (SELECT COALESCE(max(seq),0)+1 AS seq FROM TBL_ORG)
	,#{p_seq},#{org_ordr},#{org_nm},#{p_org_nm},#{writer_id},now()
	,#{writer_ip},#{writer_id},now(),#{writer_ip},#{sido},#{gugun},#{section},
	(
		select STRING_AGG(TA.org_nm, '|' ORDER BY TA.seq ASC) || '|' || #{org_nm} AS all_org_nm 
		from (
			WITH RECURSIVE T AS (
				SELECT A.*
				FROM tbl_org A
				where A.seq = #{p_seq}
			
				UNION ALL
				
				SELECT A.*
				FROM tbl_org A,  T
				
				WHERE A.seq = T.p_seq
			)
			SELECT seq,p_seq,replace(replace(replace(replace(replace(replace(coalesce(org_nm,p_org_nm),'/',''),'_',''),'(',''),')',''),'+',''),' ','')as org_nm FROM T 
			order by T.seq asc				
		) TA )
WHERE NOT EXISTS (SELECT * FROM upsert)

mapper.select:
WITH RECURSIVE T AS ( SELECT A.* FROM tbl_org A where A.p_seq = #{seq}
UNION ALL
SELECT A.* FROM tbl_org A, T WHERE T.seq = A.p_seq ) SELECT seq,p_seq,org_nm,all_org_nm FROM T order by T.seq asc

mapper.update:
select all_org_nm
from tbl_org
where seq = #{org_seq}

update tbl_org
set all_org_nm = #{all_org_nm}
where seq = #{seq}
