mapper.select:
select seq, pc_uuid, pc_ip, pc_vpnip, pc_macaddress
from tbl_pc_mangr
where pc_uuid = #{pc_uuid}

select *
from tbl_user tu 
where trim(user_sabun) = #{sabun}

select count(*)
from tbl_pc_mangr
where pc_uuid = #{pc_uuid}

mapper.update:
update tbl_pc_mangr
set pc_change = #{pc_change}
where seq = #{seq}
