mapper.select:
select *
from tbl_org 
where trim(org_nm) = #{deptname}
and domain = #{domain}

select seq, pc_uuid, pc_ip, pc_vpnip, pc_macaddress, org_seq
from tbl_pc_mangr
where pc_uuid = #{pc_uuid}

select *
from tbl_user tu 
where trim(user_sabun) = #{sabun}

select count(*)
from tbl_pc_mangr
where pc_uuid = #{pc_uuid}

mapper.update:
update tbl_pc_mangr
set pc_change = #{pc_change}
where seq = #{seq}

mapper.select:
SELECT DISTINCT PA.count, PA.debname, PA.gubun , PA.insert_dt , PA.debver , PA.state FROM ( SELECT ROW_NUMBER() OVER( PARTITION BY TA.debname, TA.gubun ), COUNT(TA.debname) over(partition by TA.debname, TA.gubun), TA.* FROM ( SELECT ROW_NUMBER() OVER( PARTITION BY A.debname, A.pc_uuid ORDER BY A.insert_dt DESC), A.debname, A.gubun, A.insert_dt, A.state, A.pc_uuid, A.debver FROM ( SELECT DISTINCT UNNEST(regexp_split_to_array(ARRAY_TO_STRING(ARRAY_AGG(debname),',') , ',')) debname, gubun,org_seq, insert_dt, state, pc_uuid, debver FROM tbl_updt_policy_action_result WHERE --org_seq not in (8307 , 8308 ) -- and pc_uuid in ('32130f9b812b41bbade60271c5aa0375' ) --and pc_uuid in ( SELECT pc_uuid from tbl_pc_mangr where pc_os != 'W' and org_seq in ( WITH recursive search_org(seq,p_seq,org_nm,org_ordr,level,path,cycle) as ( SELECT a.seq,a.p_seq,a.org_nm,a.org_ordr,0,array[a.seq],false FROM tbl_org a WHERE a.seq = #{org_seq} UNION all SELECT a.seq,a.p_seq,a.org_nm,a.org_ordr,level+1,path || a.seq,a.seq=any(path) FROM tbl_org a, search_org so WHERE a.p_seq = so.seq and not cycle) SELECT seq FROM search_org ) ) --and gubun != 'DELETE' GROUP BY debname, gubun, org_seq, insert_dt, state, pc_uuid, debver order by insert_dt desc ) A WHERE A.debname != '' --ORDER BY pc_uuid DESC ) TA WHERE row_number = 1 ORDER BY TA.insert_dt DESC
limit 5
) PA order by PA.insert_dt desc

SELECT DISTINCT PA.count, PA.retport, (CASE WHEN PA.status = 'allow' THEN '허용' ELSE '차단' END ) as status , (CASE WHEN PA.status_yn = 'Y' THEN '완료' ELSE '미완료' END ) as status_yn, PA.rgstr_date FROM ( SELECT ROW_NUMBER() OVER( PARTITION BY TA.retport, TA.status ), COUNT(TA.retport) over(partition by TA.retport, TA.status), TA.* FROM ( SELECT ROW_NUMBER() OVER( PARTITION BY A.retport, A.pc_uuid ORDER BY A.rgstr_date DESC), A.retport, A.rgstr_date, A.status, A.status_yn FROM ( SELECT DISTINCT UNNEST(regexp_split_to_array(ARRAY_TO_STRING(ARRAY_AGG(retport),',') , ',')) retport ,org_seq ,pc_uuid ,datetime ,pc_hostname ,status_yn ,status ,rgstr_date FROM tbl_act_firewall_log WHERE pc_uuid in ( SELECT pc_uuid from tbl_pc_mangr where pc_os != 'W' and org_seq in ( WITH recursive search_org(seq,p_seq,org_nm,org_ordr,level,path,cycle) as ( SELECT a.seq,a.p_seq,a.org_nm,a.org_ordr,0,array[a.seq],false FROM tbl_org a WHERE a.seq = #{org_seq} UNION all SELECT a.seq,a.p_seq,a.org_nm,a.org_ordr,level+1,path || a.seq,a.seq=any(path) FROM tbl_org a, search_org so WHERE a.p_seq = so.seq and not cycle) SELECT seq FROM search_org ) ) GROUP BY seq,org_seq,pc_uuid,datetime,pc_hostname,status_yn,status,retport,rgstr_date order by rgstr_date desc ) A
) TA WHERE row_number = 1 ORDER BY TA.rgstr_date DESC
limit 5
) PA

SELECT DISTINCT PA.count, PA.product||'('||PA.vendorcode||':'||PA.productcode||')' as product, (CASE WHEN PA.status = 'Y' THEN '허용' ELSE '차단' END ) as status, PA.rgstr_date FROM ( SELECT ROW_NUMBER() OVER( PARTITION BY TA.product, TA.status ), COUNT(TA.product) over(partition by TA.product, TA.status), TA.* FROM ( SELECT ROW_NUMBER() OVER( PARTITION BY A.product, A.pc_uuid ORDER BY A.rgstr_date DESC), A.product, A.rgstr_date, A.status,A.vendorcode,A.productcode FROM ( SELECT DISTINCT UNNEST(regexp_split_to_array(ARRAY_TO_STRING(ARRAY_AGG(product),',') , ',')) product ,pc_uuid ,pc_hostname ,status ,rgstr_date ,vendorcode ,productcode FROM tbl_act_device_log WHERE pc_uuid in ( SELECT pc_uuid from tbl_pc_mangr where pc_os != 'W' and org_seq in ( WITH recursive search_org(seq,p_seq,org_nm,org_ordr,level,path,cycle) as ( SELECT a.seq,a.p_seq,a.org_nm,a.org_ordr,0,array[a.seq],false FROM tbl_org a WHERE a.seq = #{org_seq} UNION all SELECT a.seq,a.p_seq,a.org_nm,a.org_ordr,level+1,path || a.seq,a.seq=any(path) FROM tbl_org a, search_org so WHERE a.p_seq = so.seq and not cycle) SELECT seq FROM search_org ) ) GROUP BY seq,pc_uuid,pc_hostname,status,product,rgstr_date,vendorcode,productcode order by rgstr_date desc ) A ) TA WHERE row_number = 1 ORDER BY TA.rgstr_date DESC
limit 5
) PA

mapper.update:
UPDATE TBL_PC_MANGR
