mapper.update:
UPDATE tbl_policy_package_list 
	SET 
DEB_NOW_VERSION = (SELECT DEB_NEW_VERSION FROM tbl_policy_package_list WHERE PU_NAME = #{package})
,DEB_NEW_VERSION = #{version}
,PU_STATUS = 'U'
WHERE PU_NAME = #{package}

update.foreach:
update 
tbl_progrm_applc 
set ppm_seq = (select string_agg(A.ppm_seq,',')::text from 
	(
	select unnest(string_to_array(ppm_seq,',')) as ppm_seq from tbl_progrm_applc where org_seq=#{item.org_seq} and domain = #{domain}
	intersect
	select unnest(string_to_array(ppm_seq,',')) as ppm_seq from tbl_updt_applc where org_seq=#{item.org_seq} and domain = #{domain}
	) A),
	ppm_name = (select string_agg(A.ppm_name,',')::text from 
	(
	select unnest(string_to_array(ppm_name,',')) as ppm_name from tbl_progrm_applc where org_seq=#{item.org_seq} and domain = #{domain}
	intersect
	select unnest(string_to_array(ppm_name,',')) as ppm_name from tbl_updt_applc where org_seq=#{item.org_seq} and domain = #{domain}
	) A)
	 where org_seq = #{item.org_seq} and domain = #{domain}

mapper.select:
INSERT INTO TBL_ACT_UPDT_LOG ( DEB_NAME, KIND, ORG_SEQ, PC_HOSTNAME, PC_UUID, RGSTR_DATE, STATUS) VALUES
(#{item.org_seq},#{ppm_seq},#{domain})
SELECT pc_hostname,pc_uuid FROM TBL_PC_MANGR WHERE ORG_SEQ IN (
#{item.org_seq}
) AND DOMAIN = #{domain}

mapper.delete:
DELETE FROM TBL_UPDT_APPLC WHERE ORG_SEQ IN (

					#{item.org_seq}

) AND domain = #{domain}
