mapper.insert:
with upsert as 
(
	update tbl_svrlst set 
		svr_nm = #{svr_nm}
		,svr_ip = #{svr_ip}
		,svr_vip = #{svr_vip}
		,svr_port = #{svr_port}
	where
		seq = #{seq}
	returning *
)
insert into tbl_svrlst
(
	svr_nm,
	svr_ip,
	svr_vip,
	svr_port
)
select 
	#{svr_nm},
	#{svr_ip},
	#{svr_vip},
	#{svr_port}
where not exists(select * from upsert);

mapper.update:
update
	TBL_PROGRM_UDPT
set 
	polling_tm = #{polling_tm}
where 
	pu_name = #{pu_name}
