mapper.select:
SELECT COUNT(USER_ID)
FROM TBL_USER
WHERE USER_ID = #{user_id}

SELECT COUNT(sabun)
FROM TBL_USER
WHERE user_sabun = #{sabun}

mapper.delete:

DELETE FROM TBL_USER 
WHERE seq IN

	#{item.seq}

mapper.update:
UPDATE TBL_USER SET user_id = #{user_id},
pass_wd = #{pass_wd},
user_name = #{user_name}, kind = #{kind}, rank = #{rank}, email = #{email}, tel = #{tel}, org_seq = #{org_seq}, position = #{position}, user_sabun = #{user_sabun}, upd_date = now() WHERE user_id = #{user_id}

mapper.select:

select all_org_nm 
from tbl_org 
where seq = #{seq}
