USER_NAME LIKE '%' || #{txtSearch} || '%'
(SELECT ORG_NM FROM TBL_ORG WHERE SEQ = ARR_ORG_SEQ::int) = #{txtSearch}
(USER_ID LIKE '%' || #{txtSearch} || '%' OR USER_NAME LIKE '%' || #{txtSearch} || '%' )
ORDER BY insert_dt DESC OFFSET #{limitStart} LIMIT #{recordSize}

SELECT count(*) as tbl_cnt FROM TBL_MANAGER
USER_ID LIKE '%' || #{txtSearch} || '%'
USER_NAME LIKE '%' || #{txtSearch} || '%'
(SELECT ORG_NM FROM TBL_ORG WHERE SEQ = ARR_ORG_SEQ::int) = #{txtSearch}
(USER_ID LIKE '%' || #{txtSearch} || '%' OR USER_NAME LIKE '%' || #{txtSearch} || '%'
)

