mapper.insert:
INSERT INTO tbl_security_agentjob (pcm_seq, syname, systatus, org_seq, sm_gubun, sm_port) select sm_seq, sm_name, 'Y', #{org_seq} , sm_gubun, sm_port from tbl_security_mngr where sm_seq in (
${orgpcmseq[index]}
)

mapper.select:
select ta.sm_seq, ta.sm_gubun, ta.sm_port, trim(ta.sm_name) as sm_name, trim(tb.systatus) as pcm_status from tbl_security_mngr ta left outer join ( select pcm_seq, syname , systatus from tbl_security_agentjob where org_seq = '3' ) tb on ta.sm_seq = tb.pcm_seq where ta.sm_seq in (
${orgpcmseq[index]}
)

select pcm_seq, sm_gubun, sm_port, syname as pcm_name, systatus as pcm_status from tbl_security_agentjob where org_seq = '3' and pcm_seq not in (
${orgpcmseq[index]}
)

mapper.delete:
DELETE FROM tbl_security_agentjob 
WHERE 
		pcm_seq = #{pcm_seq}
		and org_seq = #{org_seq}

DELETE FROM tbl_progrm_agentjbo 
WHERE 
		pcm_seq = #{pcm_seq}
		and org_seq = #{org_seq}

mapper.select:
select ta.pcm_seq, ta.pcm_name, tb.pstatus as pcm_status from tbl_program_mngr ta left outer join ( select pcm_seq, pname , pstatus from tbl_progrm_agentjbo where org_seq = '3' ) tb on ta.pcm_seq = tb.pcm_seq where ta.pcm_seq in (
${orgpcmseq[index]}
)

select pcm_seq, pname as pcm_name, pstatus as pcm_status from tbl_progrm_agentjbo where org_seq = '3' and pcm_seq not in (
${orgpcmseq[index]}
)

mapper.insert:
		INSERT INTO
    	tbl_progrm_agentjbo (pcm_seq, pname, pstatus, org_seq)  
		select pcm_seq, pcm_name, 'Y', #{org_seq} 
			from tbl_program_mngr
			where pcm_seq in ( 

			        ${orgpcmseq[index]}

	            )
